import UIKit

protocol MovieDetailDisplayLogic: AnyObject {
    func displayInitializeResult(viewModel: MovieDetail.Initialize.ViewModel)
    func displayReloadResult(viewModel: MovieDetail.Reload.ViewModel)
    func displayFinalizeResult(viewModel: MovieDetail.Finalize.ViewModel)
}

class MovieDetailViewController: BaseViewController, MovieDetailDisplayLogic {
    var interactor: MovieDetailBusinessLogic?
    var router: (NSObjectProtocol & MovieDetailRoutingLogic & MovieDetailDataPassing)?

    // MARK: Outlets
    
    @IBOutlet weak var imageViewMovie: UIImageView!
    @IBOutlet weak var textViewMovieDetails: UITextView!
    @IBOutlet weak var indicatorView: UIActivityIndicatorView!
    @IBOutlet weak var labelNotFetchedMovie: UILabel!
    @IBOutlet weak var constraitImageHeight: NSLayoutConstraint!
    
    // MARK: lifecycle
    
    override func viewDidLoad() {
        
        super.viewDidLoad()
        imageViewMovie.layer.cornerRadius = LodosMoviesRadiusConstants.mediumCornerRadius
        interactor?.reload(request: MovieDetail.Reload.Request())
    }

    // MARK: Object lifecycle

    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }

    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup Pattern

    private func setup() {

        let viewController = self
        let interactor = MovieDetailInteractor()
        let presenter = MovieDetailPresenter()
        let router = MovieDetailRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }

    func setPageComponents(moviewDetail: MovieDetailResponse) {
        
        textViewMovieDetails.attributedText = moviewDetail.toAttributedString()
    }

}

// MARK: - Display Logic

extension MovieDetailViewController {
    
    func displayInitializeResult(viewModel: MovieDetail.Initialize.ViewModel) { }

    func displayReloadResult(viewModel: MovieDetail.Reload.ViewModel) { 
        
        if viewModel.isContinue {
            indicatorView.unHiddenAndStartAnimation()
        } else {
            indicatorView.hiddenAndStopAnimation()
        }
        
        if let movieDetail = viewModel.movieDetail {
            labelNotFetchedMovie.isHidden = true
            setImage(imagePath: movieDetail.poster)
            setPageComponents(moviewDetail: movieDetail)
        } else {
            labelNotFetchedMovie.isHidden = false
            labelNotFetchedMovie.text = UIMessageConstant.detailNotFound
        }
    }

    func displayFinalizeResult(viewModel: MovieDetail.Finalize.ViewModel) { }
}

// MARK: - Image Logics

extension MovieDetailViewController {
    
    private func setImage(imagePath: String?) {

        guard let imagePath = imagePath, let url = URL(string: imagePath) else {
            setBrokenImage()
            print("Failed to present attachment due to an invalid url: ")
            return
        }
        imageViewMovie.image  = nil
        imageViewMovie.isHidden = true
        textViewMovieDetails.isHidden = true
        indicatorView.unHiddenAndStartAnimation()
        
        let task = URLSession.shared.dataTask(with: url, completionHandler: { (data, response, error) in
            if error == nil {
                guard let unwrappedData = data, let image = UIImage(data: unwrappedData) else { return }
                DispatchQueue.main.async {
                    self.constraitImageHeight.constant = image.size.height
                    self.indicatorView.hiddenAndStopAnimation()
                    self.imageViewMovie.isHidden = false
                    self.imageViewMovie.image = image
                    self.textViewMovieDetails.isHidden = false
                }
            } else {
                self.setBrokenImage()
            }
        })
        task.resume()
    }
    
    private func setBrokenImage() {
        
        imageViewMovie.image = LocaleImage.brokenImage.getUIImage()
    }
}
